INSERT INTO `faqs` (`id`, `question`, `answer`, `category`, `priority`, `views`, `helpful_count`, `status`, `createdAt`, `updatedAt`) VALUES ('faq_46f1037b3fa632ee', 'Do you deliver equipment?', 'Yes, we provide delivery services throughout Cambodia. Delivery charges depend on location and equipment size. Contact us for a delivery quote.', 'Delivery', 70, 0, 0, 'PUBLISHED', '2025-11-26 22:58:06', '2025-11-26 22:58:06');
INSERT INTO `faqs` (`id`, `question`, `answer`, `category`, `priority`, `views`, `helpful_count`, `status`, `createdAt`, `updatedAt`) VALUES ('faq_723f6cae5c33cfc3', 'Do you provide installation services?', 'Yes, we provide professional installation services for all our equipment. Our trained technicians will ensure proper setup and provide training for your staff.', 'Services', 95, 0, 0, 'PUBLISHED', '2025-11-26 22:58:06', '2025-11-26 22:58:06');
INSERT INTO `faqs` (`id`, `question`, `answer`, `category`, `priority`, `views`, `helpful_count`, `status`, `createdAt`, `updatedAt`) VALUES ('faq_83bdccfdde778b6a', 'What is your warranty policy?', 'All our equipment comes with a comprehensive warranty. Standard warranty is 12 months for parts and labor. Extended warranty options are also available.', 'Warranty', 90, 0, 0, 'PUBLISHED', '2025-11-26 22:58:06', '2025-11-26 22:58:06');
INSERT INTO `faqs` (`id`, `question`, `answer`, `category`, `priority`, `views`, `helpful_count`, `status`, `createdAt`, `updatedAt`) VALUES ('faq_873c1a2408d5eb8d', 'Can I trade in my old equipment?', 'Yes, we accept trade-ins for used equipment. We will evaluate your equipment and provide a fair trade-in value that can be applied to your new purchase.', 'Trade-in', 55, 0, 0, 'PUBLISHED', '2025-11-26 22:58:06', '2025-11-26 22:58:06');
INSERT INTO `faqs` (`id`, `question`, `answer`, `category`, `priority`, `views`, `helpful_count`, `status`, `createdAt`, `updatedAt`) VALUES ('faq_91092c775de3ccb4', 'Do you provide training for equipment operation?', 'Yes, we provide comprehensive training for all equipment operators. Training includes safety procedures, operation techniques, and basic maintenance.', 'Training', 60, 0, 0, 'PUBLISHED', '2025-11-26 22:58:06', '2025-11-26 22:58:06');
INSERT INTO `faqs` (`id`, `question`, `answer`, `category`, `priority`, `views`, `helpful_count`, `status`, `createdAt`, `updatedAt`) VALUES ('faq_ab9ec43d1c6a12a4', 'What is the lead time for orders?', 'Lead times vary depending on the product and stock availability. Most standard items are available within 1-2 weeks. Custom orders may take 4-6 weeks.', 'Orders', 65, 0, 0, 'PUBLISHED', '2025-11-26 22:58:06', '2025-11-26 22:58:06');
INSERT INTO `faqs` (`id`, `question`, `answer`, `category`, `priority`, `views`, `helpful_count`, `status`, `createdAt`, `updatedAt`) VALUES ('faq_ac30e40a6bd32856', 'What types of forklifts do you offer?', 'We offer a wide range of forklifts including electric, diesel, and LPG-powered models. Our forklifts range from 1.5 to 10 tons capacity, suitable for various industrial applications.', 'Products', 100, 0, 0, 'PUBLISHED', '2025-11-26 22:58:06', '2025-11-26 22:58:06');
INSERT INTO `faqs` (`id`, `question`, `answer`, `category`, `priority`, `views`, `helpful_count`, `status`, `createdAt`, `updatedAt`) VALUES ('faq_cb823fc0c925bcc7', 'Can I get a quote online?', 'Absolutely! You can request a quote through our website by filling out the quote request form. Our sales team will contact you within 24 hours with a detailed quotation.', 'Sales', 80, 0, 0, 'PUBLISHED', '2025-11-26 22:58:06', '2025-11-26 22:58:06');
INSERT INTO `faqs` (`id`, `question`, `answer`, `category`, `priority`, `views`, `helpful_count`, `status`, `createdAt`, `updatedAt`) VALUES ('faq_e1d9bb0720c44261', 'Do you offer maintenance services?', 'Yes, we provide regular maintenance services to keep your equipment running smoothly. We offer scheduled maintenance plans and emergency repair services.', 'Services', 85, 0, 0, 'PUBLISHED', '2025-11-26 22:58:06', '2025-11-26 22:58:06');
